$(document).ready(function(){

  // Add img-full class to post images
	$(".post-body img, .post-body img a").addClass("img-full");

  // Replace widget h2 tag 
	$("#sidebar-wrapper, #footer").find('h2').replaceWith(function() {
		return '<h3>' + $(this).text() + '</h3>';
	});
  // Image helper for Add to Cart
  	$("img[alt*='product']").addClass("item_thumbimg");
 	$('#thumb img').addClass ("item_thumbimg"); 

	$('#cart-icon').click(function() {
		$("html , body").animate({scrollTop:0}, "slow");
		$('.cart-details-wrapper').slideToggle('slow');
      $('.cart-details-wrapper').css('display: static;');
        return false;
	});

	$('#nav-second').click(function() {
      $('.Label ul').slideToggle('slow');
        return false;
	});

	$('#nav-main').click(function() {
      $('.LinkList ul').slideToggle('slow');
        return false;
	});

  $(".featured").append("<apan class='special-featured'><h3>Featured of the Day &rarr;</h3><span class='special-featured1'>While Stock Last</span></span>");

  $(".offers").append("<apan class='special-offers'><h3>Discounts Up To 20% &rarr;</h3><span class='special-offers1'>While Stock Last</span></span>");
}); 

  // Back to top  slow scroll  
$(window).ready(function(){
	$("#backtotop" ).click(function(e){
    e.preventDefault();
    $("html , body").animate({scrollTop:0}, "slow");
    });
});

